mapper.select:

select * from (
					select rownum as rnum, a.* 
	  		from ( 
	    		select * from board where bcat_num = 1 and member_id = #{member_id} order by b_index desc
	   	    )a where rownum <= #{pageNum} * #{amount}
	 	) where rnum > (#{pageNum}-1) * #{amount}


select * from (
					select rownum as rnum, a.* 
	  		from ( 
	    		select * from board where bcat_num = 2 and member_id = #{member_id} order by b_index desc
	   	    )a where rownum <= #{pageNum} * #{amount}
	 	) where rnum > (#{pageNum}-1) * #{amount}




	select * from (  
select rownum as rn, a.* from ( 
        select b.IDNT_NUM, b.icat_num, b.member_id, b.idntdate, a.INAME, a.iamount,b.donaamount 
from iteminven a, itemdonation b where a.icat_num = b.icat_num and  and b.member_id = #{member_id}  order by b.idntdate desc
     )a where rownum <=#{pageNum} * #{amount}
) where rn >  (#{pageNum}-1) * #{amount}


	select * from (  
select rownum as rn, a.* from ( 
        select a.rcontent, a.rdate, a.rid, b.b_index, b.btitle, b.bcat_num
from boardreply a, board b where a.b_index = b.b_index and a.rid = #{rid} order by a.rdate
     )a where rownum <=#{pageNum} * #{amount}
) where rn >  (#{pageNum}-1) * #{amount}


select * from (
					select rownum as rnum, a.* 
	  		from ( 
	    		select * from donation order by dntdate desc
	   	    )a where rownum <= #{pageNum} * #{amount}
	 	) where rnum > (#{pageNum}-1) * #{amount}

mapper.update:

UPDATE member
SET name = #{name}, pw = #{pw},subemail = #{subemail}
where member_id = #{member_id}

mapper.delete:
UPDATE member
SET enable = 2
where member_id = #{member_id} and name=#{name}
