mapper.select:

			select * from (
				select  
    					rownum as rn, a.* from ( 
        					select /*+ INDEX_DESC(board pk_board) */ * from board where bcat_num = 2 
     			) a where 






							btitle like '%'||#{keyword}||'%'


							bcontent like '%'||#{keyword}||'%'


							member_id like '%'||#{keyword}||'%'






		   	    rownum <= #{pageNum} * #{amount}
		 	) where rn > (#{pageNum}-1) * #{amount}
